import 'package:flutter/material.dart';import 'package:get/get.dart';class DashboardController extends GetxController {  String selectedFilter = "Today";  bool note = false;  onNote() {    note = !note;    update();  }  List filters = [    {      "icon": Icons.today,      "label": "Today",      "isActive": true,    },    {      "icon": Icons.calendar_view_week,      "label": "Week",      "isActive": false,    },    {      "icon": Icons.calendar_view_month,      "label": "Month",      "isActive": false,    },  ];  onChange(int i) {    for (int a = 0; a < filters.length; a++) {      if (a == i) {        filters[a]["isActive"] = true;        selectedFilter = filters[a]["label"];      } else {        filters[a]["isActive"] = false;      }    }    update();  }}