import 'package:flutter/material.dart';import 'package:fw_vendor/core/configuration/app_routes.dart';import 'package:fw_vendor/core/theme/index.dart';import 'package:get/get.dart';class GlobalDirectoryController extends GetxController {  bool isSearch = false;  onSearch() {    isSearch = !isSearch;    update();  }  List selectedAddress = [    for (int i = 1; i < 11; i++)      {        "shopName": "Siya Medical=$i",        "personName": "R7-2140$i",        "number": "9825941092",        "address": "R-7, C-201 2Nd Floor Astha Square Nr.Kapodra Utran Bridge V.I.P Circle Satyam Hospital Utran Surat Utran.",        "area": "SAIYADPURA-MAHIDHARPURA-BHAGAL-NANPURA-ALTHWAGATE-RINGROAD-SALABATPURA",        "selected": false      }  ];  List selectedOrderTrueList = [];  onSelectedLocation() {    Get.toNamed(AppRoutes.globalDirectoryDetailsScreen);    update();  }  addToSelectedList(item) {    if (item != null) {      var index = selectedOrderTrueList.indexOf(item);      if (index == -1) {        selectedOrderTrueList.add(item);        update();      }      _autoSelector();    }  }  removeToSelectedList(item) {    if (item != null) {      Get.dialog(        AlertDialog(          title: Text(            'Remove',            style: AppCss.h1,          ),          content: Text(            'Do you remove this location?',            style: AppCss.h3,          ),          actions: [            TextButton(              child: const Text("Ok"),              onPressed: () {                selectedOrderTrueList.remove(item);                _autoSelector();                Get.back();              },            ),            TextButton(              child: const Text("Close"),              onPressed: () => Get.back(),            ),          ],        ),      );    }  }  _autoSelector() {    for (int i = 0; i < selectedAddress.length; i++) {      var data = selectedOrderTrueList.where((element) => element['personName'] == selectedAddress[i]['personName']);      if (data.isNotEmpty) {        selectedAddress[i]['selected'] = true;      } else {        selectedAddress[i]['selected'] = false;      }      update();    }  }  onSaveLocation() {    Get.toNamed(AppRoutes.saveAddressScreen);    update();  }}