import 'package:flutter/material.dart';import 'package:fw_vendor/common/config.dart';import 'package:fw_vendor/core/configuration/app_routes.dart';import 'package:fw_vendor/core/utilities/index.dart';import 'package:fw_vendor/networking/index.dart';import 'package:get/get.dart';class LoginController extends GetxController {  TextEditingController emailController = TextEditingController();  TextEditingController passwordController = TextEditingController();  final formKey = GlobalKey<FormState>();  bool isSlider = false;  bool isVisible = false;  bool isRemember = false;  void updateStatus() {    isVisible = !isVisible;    update();  }  void onLoginButton() async {    try {      var resData = await apis.call(        apiMethods.login,        {          "emailId": emailController.text,          "password": passwordController.text,          "loginWith": "emailId",        },        ApiType.post,      );      if (resData.isSuccess == true && resData.data != 0) {        writeStorage(Session.userData, resData.data[0]);        writeStorage(Session.authToken, resData.data[0]['token']);        Get.toNamed(AppRoutes.home);      } else {        Get.snackbar(          "Error",          "Please try again ?",          backgroundColor: Colors.white,          colorText: Colors.black,        );      }    } catch (e) {      throw Exception("Failed to load users");    }  }  onRemember() {    isRemember = !isRemember;    update();  }}