import 'package:fw_vendor/common/config.dart';import 'package:fw_vendor/core/configuration/app_routes.dart';import 'package:fw_vendor/core/utilities/index.dart';import 'package:get/get.dart';class SplashController extends GetxController {  String? version;  @override  void onReady() {    Future.delayed(const Duration(seconds: 2), () async {      var userData = await getStorage(Session.userData);      if (userData != null && userData != 0) {        Get.toNamed(AppRoutes.home);      } else {        Get.toNamed(AppRoutes.login);      }    });    super.onReady();  }}