import 'package:flutter/material.dart';import 'package:get/get.dart';snackBar(String message, Color? color) {  return Get.rawSnackbar(    title: null,    messageText: Text(      message,      style: const TextStyle(color: Colors.white),    ),    backgroundColor: color ?? Colors.black,    snackPosition: SnackPosition.TOP,    borderRadius: 0,    margin: const EdgeInsets.all(0),  );}