import 'package:flutter/material.dart';import 'package:fw_vendor/controller/app_controller.dart';import 'package:fw_vendor/core/theme/index.dart';import 'package:get/get.dart';class CommonNoteCard extends StatelessWidget {  final double? height;  final String? text;  final bool? note;  const CommonNoteCard({    Key? key,    this.height,    this.text,    this.note,  }) : super(key: key);  @override  Widget build(BuildContext context) {    return Container(      height: height,      width: double.infinity,      decoration: BoxDecoration(        boxShadow: kElevationToShadow[1],        color: Colors.white,        borderRadius: BorderRadius.circular(8),      ),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Stack(            children: [              Text(                "Note",                style: AppCss.h1.copyWith(                  fontWeight: FontWeight.bold,                  color: AppController().appTheme.primary1.withOpacity(0.3),                ),              ),              Text(                "*",                style: AppCss.h1.copyWith(                  color: Colors.red,                  fontSize: 15,                ),              ).paddingOnly(left: MediaQuery.of(context).size.width * 0.12),            ],          ),          Text(            text ?? '',            strutStyle: const StrutStyle(leading:0.1),            style: AppCss.footnote.copyWith(              wordSpacing: 1,              fontSize: 13,            ),            overflow: note! ? TextOverflow.ellipsis : TextOverflow.clip,          ).paddingOnly(top: 15),        ],      ).paddingSymmetric(horizontal: 10, vertical: 15),    );  }}