// ignore_for_file: non_constant_identifier_namesimport 'package:flutter/material.dart';import 'package:fw_vendor/core/theme/index.dart';Widget CustomStatisticsCard({  dynamic onTap,  dynamic icon,  dynamic height,  dynamic width,  String? title,  String? distrction,  context,}) {  return GestureDetector(    onTap: onTap,    child: Container(      padding: const EdgeInsets.only(top: 15, left: 8, bottom: 15),      decoration: BoxDecoration(        boxShadow: kElevationToShadow[1],        color: Colors.white,        borderRadius: BorderRadius.circular(8),      ),      child: Stack(        children: [          Column(            mainAxisSize: MainAxisSize.min,            crossAxisAlignment: CrossAxisAlignment.start,            children: [              Container(                padding: const EdgeInsets.only(left: 5),                child: Text(                  title ?? '',                  style: AppCss.h2.copyWith(fontSize: 15),                ),              ),              const SizedBox(height: 15),              Row(                children: [                  Card(                    elevation: 1,                    color: Theme.of(context).primaryColor,                    child: SizedBox(                      height: 30,                      width: 30,                      child: Icon(                        icon,                        color: Colors.white,                        size: 18,                      ),                    ),                  ),                  const SizedBox(width: 5),                  Text(                    distrction ?? "",                    style: AppCss.body1.copyWith(fontWeight: FontWeight.bold, fontSize: 18),                  ),                ],              ),            ],          ),          Container(            alignment: Alignment.topRight,            margin: const EdgeInsets.only(right: 10),            child: Icon(              Icons.arrow_forward,              color: Theme.of(context).primaryColor,              size: 20,            ),          ),        ],      ),    ),  );}